18:     
19:     struct Game {
20:         uint expireTime;
21:         address dealer;
22:         uint dealerValue;
23:         bytes32 dealerHash;
24:         uint8 dealerChoice;
25:         address player;
26:         uint8 playerChoice;
27:         uint playerValue;
28:         uint8 result;
