33:     mapping (uint => mapping(uint => uint8)) public payoff;
34:     mapping (uint => Game) public games;
35:     mapping (address => uint[]) public gameidsOf;
36: 
37:     
38:     uint public maxgame = 0;
39:     uint public expireTimeLimit = 30 minutes;
40: 
41:     
42:     function RpsGame() {
43:         payoff[ROCK][ROCK] = DRAW;
44:         payoff[ROCK][PAPER] = PLAYERWIN;
45:         payoff[ROCK][SCISSORS] = DEALERWIN;
46:         payoff[PAPER][ROCK] = DEALERWIN;
47:         payoff[PAPER][PAPER] = DRAW;
48:         payoff[PAPER][SCISSORS] = PLAYERWIN;
49:         payoff[SCISSORS][ROCK] = PLAYERWIN;
50:         payoff[SCISSORS][PAPER] = DEALERWIN;
51:         payoff[SCISSORS][SCISSORS] = DRAW;
52:         payoff[NONE][NONE] = DRAW;
53:         payoff[ROCK][NONE] = DEALERWIN;
54:         payoff[PAPER][NONE] = DEALERWIN;
55:         payoff[SCISSORS][NONE] = DEALERWIN;
56:         payoff[NONE][ROCK] = PLAYERWIN;
57:         payoff[NONE][PAPER] = PLAYERWIN;
58:         payoff[NONE][SCISSORS] = PLAYERWIN;
59: 
60:         ceoAddress = msg.sender;
61:         cooAddress = msg.sender;
62:         cfoAddress = msg.sender;
63:     }
64: 
65:     
66:     function createGame(bytes32 dealerHash, address player) public payable whenNotPaused returns (uint){
67:         require(dealerHash != 0x0);
68: 
69:         maxgame += 1;
70:         Game storage game = games[maxgame];
71:         game.dealer = msg.sender;
72:         game.player = player;
73:         game.dealerHash = dealerHash;
74:         game.dealerChoice = NONE;
75:         game.dealerValue = msg.value;
76:         game.expireTime = expireTimeLimit + now;
77: 
78:         gameidsOf[msg.sender].push(maxgame);
79: 
80:         emit CreateGame(maxgame, game.dealer, game.dealerValue);
81: 
82:         return maxgame;
83:     }
84: 
85:     
86:     function joinGame(uint gameid, uint8 choice) public payable whenNotPaused returns (uint){
87:         Game storage game = games[gameid];
88: 
89:         require(msg.value == game.dealerValue && game.dealer != address(0) && game.dealer != msg.sender && game.playerChoice==NONE);
90:         require(game.player == address(0) || game.player == msg.sender);
91:         require(!game.closed);
92:         require(now < game.expireTime);
93:         require(checkChoice(choice));
94: 
95:         game.player = msg.sender;
96:         game.playerChoice = choice;
97:         game.playerValue = msg.value;
98:         game.expireTime = expireTimeLimit + now;
99: 
100:         gameidsOf[msg.sender].push(gameid);
101: 
102:         emit JoinGame(gameid, game.player, game.playerValue);
103: 
104:         return gameid;
105:     }
106: 
107:     
108:     function reveal(uint gameid, uint8 choice, bytes32 randomSecret) public returns (bool) {
109:         Game storage game = games[gameid];
110:         bytes32 proof = getProof(msg.sender, choice, randomSecret);
111: 
112:         require(!game.closed);
113:         require(now < game.expireTime);
114:         require(game.dealerHash != 0x0);
115:         require(checkChoice(choice));
116:         require(checkChoice(game.playerChoice));
117:         require(game.dealer == msg.sender && proof == game.dealerHash );
118: 
119:         game.dealerChoice = choice;
120: 
121:         Reveal(gameid, msg.sender, choice);
122: 
123:         close(gameid);
124: 
125:         return true;
126:     }
127: 
128:     
129:     function close(uint gameid) public returns(bool) {
130:         Game storage game = games[gameid];
131: 
132:         require(!game.closed);
133:         require(now > game.expireTime || (game.dealerChoice != NONE && game.playerChoice != NONE));
134: 
135:         uint8 result = payoff[game.dealerChoice][game.playerChoice];
136: 
137:         if(result == DEALERWIN){
138:             require(game.dealer.send(amountWithTip(safeAdd(game.dealerValue, game.playerValue))));
139:         }else if(result == PLAYERWIN){
140:             require(game.player.send(amountWithTip(safeAdd(game.dealerValue, game.playerValue))));
141:         }else if(result == DRAW){
142:             require(game.dealer.send(game.dealerValue) && game.player.send(game.playerValue));
143:         }
144: 
145:         game.closed = true;
146:         game.result = result;
147: 
148:         emit CloseGame(gameid, game.dealer, game.player, result);
149: 
150:         return game.closed;
151:     }
152: 
153: 
154:     function getProof(address sender, uint8 choice, bytes32 randomSecret) public view returns (bytes32){
155:         return sha3(sender, choice, randomSecret);
156:     }
157: 
158:     function gameCountOf(address owner) public view returns (uint){
159:         return gameidsOf[owner].length;
160:     }
161: 
162:     function checkChoice(uint8 choice) public view returns (bool){
163:         return choice==ROCK||choice==PAPER||choice==SCISSORS;
164:     }
