861:     function matchBytes32Prefix(bytes32 content, bytes prefix) internal returns (bool){
862:         bool match_ = true;
863:         
864:         for (var i=0; i<prefix.length; i++){
865:             if (content[i] != prefix[i]) match_ = false;
866:         }
867:         
868:         return match_;
869:     }
