949:          
950:          
951:          
952:          
953:          
954: 
955:          
956:         bool ret;
957:         address addr;
958: 
959:         assembly {
960:             let size := mload(0x40)
961:             mstore(size, hash)
962:             mstore(add(size, 32), v)
963:             mstore(add(size, 64), r)
964:             mstore(add(size, 96), s)
965: 
966:              
967:              
968:             ret := call(3000, 1, 0, size, 128, size, 32)
969:             addr := mload(size)
970:         }
971:   
972:         return (ret, addr);
973:     }
