977:         bytes32 r;
978:         bytes32 s;
979:         uint8 v;
980: 
981:         if (sig.length != 65)
982:           return (false, 0);
983: 
984:          
985:          
986:          
987:         assembly {
988:             r := mload(add(sig, 32))
989:             s := mload(add(sig, 64))
990: 
991:              
992:              
993:              
994:             v := byte(0, mload(add(sig, 96)))
995: 
996:              
997:              
998:              
999:              
1000:         }
1001: 
1002:          
1003:          
1004:          
1005:          
1006:          
1007:         if (v < 27)
1008:           v += 27;
1009: 
1010:         if (v != 27 && v != 28)
1011:             return (false, 0);
1012: 
1013:         return safer_ecrecover(hash, v, r, s);
1014:     }
