438:     function isWinningBet(Bet thisBet, uint numberRolled)
439:         private
440:         onlyWinningBets(numberRolled) {
441: 
442:         uint winAmount = (thisBet.amountBet * (10000 - edge)) / pwin;
443:         LOG_BetWon(thisBet.playerAddress, numberRolled, winAmount);
444:         safeSend(thisBet.playerAddress, winAmount);
445: 
446:         //Check for overflow and underflow
447:         if ((investorsLosses + winAmount < investorsLosses) ||
448:             (investorsLosses + winAmount < thisBet.amountBet)) {
449:                 throw;
450:             }
451: 
452:         investorsLosses += winAmount - thisBet.amountBet;
453:     }
