455:     function isLosingBet(Bet thisBet, uint numberRolled)
456:         private
457:         onlyLosingBets(numberRolled) {
458: 
459:         LOG_BetLost(thisBet.playerAddress, numberRolled);
460:         safeSend(thisBet.playerAddress, 1);
461: 
462:         //Check for overflow and underflow
463:         if ((investorsProfit + thisBet.amountBet < investorsProfit) ||
464:             (investorsProfit + thisBet.amountBet < thisBet.amountBet) ||
465:             (thisBet.amountBet == 1)) {
466:                 throw;
467:             }
468: 
469:         uint totalProfit = investorsProfit + (thisBet.amountBet - 1); //added based on audit feedback
470:         investorsProfit += (thisBet.amountBet - 1)*(10000 - houseEdge)/10000;
471:         uint houseProfit = totalProfit - investorsProfit; //changed based on audit feedback
472:         safeSend(houseAddress, houseProfit);
473:     }
