1093:     function endRound(MC2datasets.EventReturns memory _eventData_)
1094:         private
1095:         returns (MC2datasets.EventReturns)
1096:     {
1097:         
1098:         uint256 _rID = rID_;
1099:         
1100:         
1101:         uint256 _winPID = round_[_rID].plyr;
1102:         uint256 _winTID = round_[_rID].team;
1103:         
1104:         
1105:         uint256 _pot = round_[_rID].pot;
1106:         
1107:         
1108:         
1109:         uint256 _win = (_pot.mul(48)) / 100;
1110:         uint256 _com = (_pot / 50);
1111:         uint256 _gen = (_pot.mul(potSplit_[_winTID].gen)) / 100;
1112:         uint256 _up = (_pot.mul(potSplit_[_winTID].up)) / 100;
1113:         uint256 _res = (((_pot.sub(_win)).sub(_com)).sub(_gen)).sub(_up);
1114:         
1115:         
1116:         uint256 _ppt = (_gen.mul(1000000000000000000)) / (round_[_rID].keys);
1117:         uint256 _dust = _gen.sub((_ppt.mul(round_[_rID].keys)) / 1000000000000000000);
1118:         if (_dust > 0)
1119:         {
1120:             _gen = _gen.sub(_dust);
1121:             _res = _res.add(_dust);
1122:         }
1123:         
1124:         
1125:         plyr_[_winPID].win = _win.add(plyr_[_winPID].win);
1126:         
1127:         
1128:         if (!address(Jekyll_Island_Inc).call.value(_com)(bytes4(keccak256("deposit()"))))
1129:         {
1130:             
1131:             
1132:             
1133:             
1134:             
1135:             
1136:             _up = _up.add(_com);
1137:             _com = 0;
1138:         }
1139:         
1140:         
1141:         round_[_rID].mask = _ppt.add(round_[_rID].mask);
1142:         
1143:         
1144:         if (_up > 0)
1145:             Divies.deposit.value(_up)();
1146:             
1147:         
1148:         _eventData_.compressedData = _eventData_.compressedData + (round_[_rID].end * 1000000);
1149:         _eventData_.compressedIDs = _eventData_.compressedIDs + (_winPID * 100000000000000000000000000) + (_winTID * 100000000000000000);
1150:         _eventData_.winnerAddr = plyr_[_winPID].addr;
1151:         _eventData_.winnerName = plyr_[_winPID].name;
1152:         _eventData_.amountWon = _win;
1153:         _eventData_.genAmount = _gen;
1154:         _eventData_.UPAmount = _up;
1155:         _eventData_.newPot = _res;
1156:         
1157:         
1158:         rID_++;
1159:         _rID++;
1160:         round_[_rID].strt = now;
1161:         round_[_rID].end = now.add(rndInit_).add(rndGap_);
1162:         round_[_rID].pot = _res;
1163:         
1164:         return(_eventData_);
1165:     }
