109: 	function parseAddr(string _a) pure internal returns (address){  
110: 		bytes memory tmp = bytes(_a);
111: 		uint160 iaddr = 0;
112: 		uint160 b1;
113: 		uint160 b2;
114: 		for (uint i=2; i<2+2*20; i+=2){
115: 			iaddr *= 256;
116: 			b1 = uint160(tmp[i]);
117: 			b2 = uint160(tmp[i+1]);
118: 			if ((b1 >= 97)&&(b1 <= 102)) b1 -= 87;
119: 			else if ((b1 >= 48)&&(b1 <= 57)) b1 -= 48;
120: 			if ((b2 >= 97)&&(b2 <= 102)) b2 -= 87;
121: 			else if ((b2 >= 48)&&(b2 <= 57)) b2 -= 48;
122: 			iaddr += (b1*16+b2);
123: 		}
124: 		return address(iaddr);
125: 	}
