127: 	function parseInt(string _a, uint _b) pure internal returns (uint) {
128: 		bytes memory bresult = bytes(_a);
129: 		uint mint = 0;
130: 		bool decim = false;
131: 		for (uint i = 0; i < bresult.length; i++) {
132: 			if ((bresult[i] >= 48) && (bresult[i] <= 57)) {
133: 				if (decim) {
134: 					if (_b == 0) break;
135: 						else _b--;
136: 				}
137: 				mint *= 10;
138: 				mint += uint(bresult[i]) - 48;
139: 			} else if (bresult[i] == 46) decim = true;
140: 		}
141: 		return mint;
142: 	}
