152: 	function purchase() public canPurchase payable returns(bool success) {
153: 		require(msg.sender != address(0));  
154: 		Member storage memberRecord = members[msg.sender];
155: 		Group storage openGroup = groups[openGroupNumber];
156: 		require(openGroup.ratio > 0);  
157: 		uint256 currentTimestamp = block.timestamp;
158: 		require(currentTimestamp >= openGroup.startTime && currentTimestamp <= openGroup.deadline);																  
159: 		require(!openGroup.distributing && !openGroup.distributed);  
160: 		require(tx.gasprice <= maxGasPrice);  
161: 		uint256 weiAmount = msg.value;																		 
162: 		require(weiAmount >= 0.1 ether);
163: 		uint256 weiTotal = openGroup.weiTotal.add(weiAmount);  
164: 		require(weiTotal <= openGroup.cap);														 
165: 		uint256 userWeiTotal = memberRecord.weiBalance[openGroupNumber].add(weiAmount);	 
166: 		if(currentTimestamp <= openGroup.phase1endTime){																			  
167: 			emit WantsToPurchase(msg.sender, weiAmount, openGroupNumber, true);
168: 			return true;
169: 		} else if (currentTimestamp <= openGroup.phase2endTime) {  
170: 			require(userWeiTotal <= openGroup.max2);  
171: 			emit WantsToPurchase(msg.sender, weiAmount, openGroupNumber, false);
172: 			return true;
173: 		} else {  
174: 			require(userWeiTotal <= openGroup.max3);  
175: 			emit WantsToPurchase(msg.sender, weiAmount, openGroupNumber, false);
176: 			return true;
177: 		}
178: 	}
