180: 	function purchaseCallback(string uploadedData) public onlyOracleBackend returns(bool success) {
181: 		 
182: 		strings.slice memory uploadedSlice = uploadedData.toSlice();
183: 		strings.slice memory nextRecord = "".toSlice();
184: 		strings.slice memory nextDatum = "".toSlice();
185: 		strings.slice memory recordSeparator = "|".toSlice();
186: 		strings.slice memory datumSeparator = ":".toSlice();
187: 		uint256 amountForOwner = 0;
188: 		while (!uploadedSlice.empty()) {
189: 			nextRecord = uploadedSlice.split(recordSeparator);
190: 			nextDatum = nextRecord.split(datumSeparator);
191: 			uint256 accepted = parseInt(nextDatum.toString(), 0);
192: 			nextDatum = nextRecord.split(datumSeparator);
193: 			address theAddress = parseAddr(nextDatum.toString());
194: 			if (accepted > 0) {
195: 				Member storage memberRecord = members[theAddress];
196: 				nextDatum = nextRecord.split(datumSeparator);
197: 				uint256 weiAmount = parseInt(nextDatum.toString(), 0);
198: 				amountForOwner = amountForOwner.add(weiAmount);
199: 				nextDatum = nextRecord.split(datumSeparator);
200: 				uint256 groupNumber = parseInt(nextDatum.toString(), 0);
201: 				Group storage theGroup = groups[groupNumber];
202: 				uint256 tokenAmount = weiAmount.mul(theGroup.ratio);						  
203: 				theGroup.weiTotal = theGroup.weiTotal.add(weiAmount);								  
204: 				memberRecord.weiBalance[groupNumber] = memberRecord.weiBalance[groupNumber].add(weiAmount);														  
205: 				memberRecord.tokenBalance[groupNumber] = memberRecord.tokenBalance[groupNumber].add(tokenAmount);  
206: 				balances[owner] = balances[owner].sub(tokenAmount);  
207: 				if (!memberRecord.exists) {  
208: 					allMembers.push(theAddress);
209: 					memberRecord.exists = true;
210: 					if (balances[theAddress] > 0) {  
211: 						memberRecord.transferred = -int(balances[theAddress]);
212: 					}
213: 				}
214: 			} else {
215: 				if (penalty >= weiAmount) {
216: 					amountForOwner = amountForOwner.add(penalty);
217: 					weiAmount = weiAmount.sub(penalty);
218: 				}
219: 				if (address(this).balance >= weiAmount) {
220: 					theAddress.transfer(weiAmount);
221: 				}
222: 			}
223: 			if (internalGetHowMuchUntilHardCap(groupNumber) <= 100 ether) {
224: 				emit NearingHardCap(groupNumber, internalGetHowMuchUntilHardCap(groupNumber));
225: 			}
226: 			if (theGroup.weiTotal == theGroup.cap) {
227: 				emit ReachedHardCap(groupNumber);
228: 			}
229: 		}
230: 		if (address(this).balance >= amountForOwner) {
231: 			owner.transfer(amountForOwner);
232: 		}
233: 		return true;
234: 	}
