410: 		uint256 n = allMembers.length;
411: 		uint256 ownerBalance = balances[msg.sender];
412: 		uint256 increaseSupplyBy = ownerBalance.mul(splitFactor).sub(ownerBalance);  
413: 		balances[msg.sender] = balances[msg.sender].mul(splitFactor);
414: 		totalSupply_ = totalSupply_.mul(splitFactor);
415: 		emit Transfer(address(0), msg.sender, increaseSupplyBy);  
416: 		for (uint256 i = 0; i < n; i++) {
417: 			Member storage currentMember = members[allMembers[i]];
418: 			 
419: 			currentMember.transferred = currentMember.transferred * int(splitFactor);
420: 			 
421: 			for (uint256 j = 0; j < nextGroupNumber; j++) {
422: 				uint256 memberBalance = currentMember.tokenBalance[j];
423: 				uint256 multiplier = memberBalance.mul(splitFactor);
424: 				currentMember.tokenBalance[j] = multiplier;
425: 			}
426: 		}
427: 		 
428: 		n = nextGroupNumber;
429: 		require(n > 0);  
430: 		for (i = 0; i < n; i++) {
431: 			Group storage currentGroup = groups[i];
432: 			currentGroup.ratio = currentGroup.ratio.mul(splitFactor);
433: 		}
434: 		emit SplitTokens(splitFactor);
435: 		return true;
436: 	}
