533: 	function transfer(address _to, uint256 _value) public onlyPayloadSize(2 * 32) canTransfer returns (bool success) {		
534: 		 
535: 		Member storage fromMember = members[msg.sender];
536: 		if (fromMember.exists) {  
537: 			int256 transferValue = fromMember.transferred + int(_value);
538: 			require(transferValue >= fromMember.transferred);  
539: 			require(transferValue <= int(getUnlockedBalanceLimit(msg.sender)));  
540: 			fromMember.transferred = transferValue;
541: 		}
542: 		 
543: 		 
544: 		if (!fromMember.exists && msg.sender != owner) {
545: 			bool fromTransferee = nonMemberTransfers[msg.sender];
546: 			if (!fromTransferee) {  
547: 				nonMemberTransfers[msg.sender] = true;
548: 				allNonMembers.push(msg.sender);
549: 			}
550: 		}
551: 		if (!members[_to].exists && _to != owner) {
552: 			bool toTransferee = nonMemberTransfers[_to];
553: 			if (!toTransferee) {  
554: 				nonMemberTransfers[_to] = true;
555: 				allNonMembers.push(_to);
556: 			}
557: 		} else if (members[_to].exists) {  
558: 			int256 transferInValue = members[_to].transferred - int(_value);
559: 			require(transferInValue <= members[_to].transferred);  
560: 			members[_to].transferred = transferInValue;
561: 		}
562: 		return super.transfer(_to, _value);
563: 	}
