11:     uint256 public minimumQuorum;           // The minimum number of tokens that must participate in a vote to achieve a quorum
12:     uint256 public debatingPeriod;          // Min time to vote for an proposal [sec]
13:     uint256 public marginForMajority;       // Min superiority of votes "for" to pass the proposal [number of tokens]
14:     // Proposals
15:     Proposal[] public proposals;
16:     uint256 public proposalsNumber = 0;
17:     mapping (address => uint32) tokensLocks;         // congress member => number of locks (recursive mutex)
18: 
19:     /* Contract Events */
20:     event ProposalAddedEvent(uint256 proposalID, address beneficiary, uint256 etherAmount, string description);
21:     event VotedEvent(uint256 proposalID, address voter, bool inSupport, uint256 voterTokens, string justificationText);
22:     event ProposalTalliedEvent(uint256 proposalID, bool quorum, bool result);
23:     event ProposalExecutedEvent(uint256 proposalID);
24:     event RulesChangedEvent(uint256 congressMemberThreshold,
25:                             uint256 minimumQuorum,
26:                             uint256 debatingPeriod,
27:                             uint256 marginForMajority);
28: 
29:     /* Contract Structures */
30:     enum ProposalState {Proposed, NoQuorum, Rejected, Passed, Executed}
31: 
32:     struct Proposal {
33:         /* Proposal content */
34:         address beneficiary;
35:         uint256 etherAmount;
36:         string description;
37:         bytes32 proposalHash;
38: 
39:         /* Proposal state */
40:         ProposalState state;
41: 
42:         /* Voting state */
43:         uint256 votingDeadline;
44:         Vote[] votes;
45:         uint256 votesNumber;
46:         mapping (address => bool) voted;
47:     }
48: 
49:     struct Vote {
50:         address voter;
51:         bool inSupport;
52:         uint256 voterTokens;
53:         string justificationText;
54:     }
55: 
56:     /* modifier that allows only congress members to vote and create new proposals */
57:     modifier onlyCongressMembers {
58:         if (dasToken.balanceOf(msg.sender) < congressMemberThreshold) throw;
59:         _;
60:     }
61: 
62:     /* Constructor */
63:     function DAS(
64:         uint256 _congressMemberThreshold,
65:         uint256 _minimumQuorum,
66:         uint256 _debatingPeriod,
67:         uint256 _marginForMajority,
68:         address _congressLeader
69:     ) payable {
70:         // create a source of democracy
71:         dasToken = new DASToken('DA$', 'DA$', 18, 1000000000 * (10 ** 18), _congressLeader);
72:         abcToken = new ABCToken('Alphabit', 'ABC', 18, 210000000 * (10 ** 18), _congressLeader);
73: 
74:         // setup rules
75:         congressMemberThreshold = _congressMemberThreshold;
76:         minimumQuorum = _minimumQuorum;
77:         debatingPeriod = _debatingPeriod;
78:         marginForMajority = _marginForMajority;
79: 
80:         RulesChangedEvent(congressMemberThreshold, minimumQuorum, debatingPeriod, marginForMajority);
81:     }
82: 
83:     // blank fallback functions to receive ETH and tokens
84:     function() payable { }
85: 
86:     function erc223Fallback(address _from, uint256 _value, bytes _data){
87:         // to avoid warnings during compilation
88:         _from = _from;
89:         _value = _value;
90:         _data = _data;
91:     }
92: 
93: 
94:     /* Proposal-related functions */
95: 
96:     // calculate hash of an proposal
97:     function getProposalHash(
98:         address _beneficiary,
99:         uint256 _etherAmount,
100:         bytes _transactionBytecode
101:     )
102:         constant
103:         returns (bytes32)
104:     {
105:         return sha3(_beneficiary, _etherAmount, _transactionBytecode);
106:     }
107: 
108:     // block tokens of an voter
109:     function blockTokens(address _voter) internal {
110:         if (tokensLocks[_voter] + 1 < tokensLocks[_voter]) throw;
111: 
112:         tokensLocks[_voter] += 1;
113:         if (tokensLocks[_voter] == 1) {
114:             dasToken.blockAccount(_voter);
115:         }
116:     }
117: 
118:     // unblock tokens of an voter
119:     function unblockTokens(address _voter) internal {
120:         if (tokensLocks[_voter] <= 0) throw;
121: 
122:         tokensLocks[_voter] -= 1;
123:         if (tokensLocks[_voter] == 0) {
124:             dasToken.unblockAccount(_voter);
125:         }
126:     }
127: 
128:     // create new proposal
129:     function createProposal(
130:         address _beneficiary,
131:         uint256 _etherAmount,
132:         string _description,
133:         bytes _transactionBytecode
134:     )
135:         onlyCongressMembers
136:         returns (uint256 _proposalID)
137:     {
138:         _proposalID = proposals.length;
139:         proposals.length += 1;
140:         proposalsNumber = _proposalID + 1;
141: 
142:         proposals[_proposalID].beneficiary = _beneficiary;
143:         proposals[_proposalID].etherAmount = _etherAmount;
144:         proposals[_proposalID].description = _description;
145:         proposals[_proposalID].proposalHash = getProposalHash(_beneficiary, _etherAmount, _transactionBytecode);
146:         proposals[_proposalID].state = ProposalState.Proposed;
147:         proposals[_proposalID].votingDeadline = now + debatingPeriod * 1 seconds;
148:         proposals[_proposalID].votesNumber = 0;
149: 
150:         ProposalAddedEvent(_proposalID, _beneficiary, _etherAmount, _description);
151: 
152:         return _proposalID;
153:     }
154: 
155:     // vote for an proposal
156:     function vote(
157:         uint256 _proposalID,
158:         bool _inSupport,
159:         string _justificationText
160:     )
161:         onlyCongressMembers
162:     {
163:         Proposal p = proposals[_proposalID];
164: 
165:         if (p.state != ProposalState.Proposed) throw;
166:         if (p.voted[msg.sender] == true) throw;
167: 
168:         var voterTokens = dasToken.balanceOf(msg.sender);
169:         blockTokens(msg.sender);
170: 
171:         p.voted[msg.sender] = true;
172:         p.votes.push(Vote(msg.sender, _inSupport, voterTokens, _justificationText));
173:         p.votesNumber += 1;
174: 
175:         VotedEvent(_proposalID, msg.sender, _inSupport, voterTokens, _justificationText);
176:     }
177: 
178:     // finish voting on an proposal
179:     function finishProposalVoting(uint256 _proposalID) onlyCongressMembers {
180:         Proposal p = proposals[_proposalID];
181: 
182:         if (now < p.votingDeadline) throw;
183:         if (p.state != ProposalState.Proposed) throw;
184: 
185:         var _votesNumber = p.votes.length;
186:         uint256 tokensFor = 0;
187:         uint256 tokensAgainst = 0;
188:         for (uint256 i = 0; i < _votesNumber; i++) {
189:             if (p.votes[i].inSupport) {
190:                 tokensFor += p.votes[i].voterTokens;
191:             }
192:             else {
193:                 tokensAgainst += p.votes[i].voterTokens;
194:             }
195: 
196:             unblockTokens(p.votes[i].voter);
197:         }
198: 
199:         if ((tokensFor + tokensAgainst) < minimumQuorum) {
200:             p.state = ProposalState.NoQuorum;
201:             ProposalTalliedEvent(_proposalID, false, false);
202:             return;
203:         }
204:         if ((tokensFor - tokensAgainst) < marginForMajority) {
205:             p.state = ProposalState.Rejected;
206:             ProposalTalliedEvent(_proposalID, true, false);
207:             return;
208:         }
209:         p.state = ProposalState.Passed;
210:         ProposalTalliedEvent(_proposalID, true, true);
211:         return;
212:     }
213: 
214:     // execute passed proposal
215:     function executeProposal(uint256 _proposalID, bytes _transactionBytecode) onlyCongressMembers {
216:         Proposal p = proposals[_proposalID];
217: 
218:         if (p.state != ProposalState.Passed) throw;
219: 
220:         p.state = ProposalState.Executed;
221:         if (!p.beneficiary.call.value(p.etherAmount * 1 ether)(_transactionBytecode)) {
222:             throw;
223:         }
224: 
225:         ProposalExecutedEvent(_proposalID);
226:     }
227: }

