776:     function core(uint256 _rID, uint256 _pID, uint256 _eth, uint256 _affID, uint256 _team, F3Ddatasets.EventReturns memory _eventData_)
777:         private
778:     {
779:         
780:         if (plyrRnds_[_pID][_rID].keys == 0)
781:             _eventData_ = managePlayer(_pID, _eventData_);
782: 
783:         
784:         if (_eth > 1000000000)
785:         {
786: 
787:             
788:             uint256 _keys = (round_[_rID].eth).keysRec(_eth);
789: 
790:             
791:             if (_keys >= 1000000000000000000)
792:             {
793:                 updateTimer(_keys, _rID);
794: 
795:                 
796:                 if (round_[_rID].plyr != _pID)
797:                     round_[_rID].plyr = _pID;
798:                 if (round_[_rID].team != _team)
799:                     round_[_rID].team = _team;
800: 
801:                 
802:                 _eventData_.compressedData = _eventData_.compressedData + 100;
803:             }
804: 
805:             
806:             if (_eth >= 100000000000000000)
807:             {
808:             airDropTracker_++;
809:             if (airdrop() == true)
810:             {
811:                 
812:                 uint256 _prize;
813:                 if (_eth >= 10000000000000000000)
814:                 {
815:                     
816:                     _prize = ((airDropPot_).mul(75)) / 100;
817:                     plyr_[_pID].win = (plyr_[_pID].win).add(_prize);
818: 
819:                     
820:                     airDropPot_ = (airDropPot_).sub(_prize);
821: 
822:                     
823:                     _eventData_.compressedData += 300000000000000000000000000000000;
824:                 } else if (_eth >= 1000000000000000000 && _eth < 10000000000000000000) {
825:                     
826:                     _prize = ((airDropPot_).mul(50)) / 100;
827:                     plyr_[_pID].win = (plyr_[_pID].win).add(_prize);
828: 
829:                     
830:                     airDropPot_ = (airDropPot_).sub(_prize);
831: 
832:                     
833:                     _eventData_.compressedData += 200000000000000000000000000000000;
834:                 } else if (_eth >= 100000000000000000 && _eth < 1000000000000000000) {
835:                     
836:                     _prize = ((airDropPot_).mul(25)) / 100;
837:                     plyr_[_pID].win = (plyr_[_pID].win).add(_prize);
838: 
839:                     
840:                     airDropPot_ = (airDropPot_).sub(_prize);
841: 
842:                     
843:                     _eventData_.compressedData += 300000000000000000000000000000000;
844:                 }
845:                 
846:                 _eventData_.compressedData += 10000000000000000000000000000000;
847:                 
848:                 _eventData_.compressedData += _prize * 1000000000000000000000000000000000;
849: 
850:                 
851:                 airDropTracker_ = 0;
852:             }
853:         }
854: 
855:             
856:             _eventData_.compressedData = _eventData_.compressedData + (airDropTracker_ * 1000);
857: 
858:             
859:             plyrRnds_[_pID][_rID].keys = _keys.add(plyrRnds_[_pID][_rID].keys);
860:             plyrRnds_[_pID][_rID].eth = _eth.add(plyrRnds_[_pID][_rID].eth);
861: 
862:             
863:             round_[_rID].keys = _keys.add(round_[_rID].keys);
864:             round_[_rID].eth = _eth.add(round_[_rID].eth);
865:             rndTmEth_[_rID][_team] = _eth.add(rndTmEth_[_rID][_team]);
866: 
867:             
868:             _eventData_ = distributeExternal(_rID, _pID, _eth, _affID, _team, _eventData_);
869:             _eventData_ = distributeInternal(_rID, _pID, _eth, _team, _keys, _eventData_);
870: 
871:             
872: 		    endTx(_pID, _team, _eth, _keys, _eventData_);
873:         }
874:     }
