840:     function core(uint256 _rID, uint256 _pID, uint256 _eth, uint256 _affID, uint256 _team, F3Ddatasets.EventReturns memory _eventData_)
841:     private
842:     {
843:         
844:         if (plyrRnds_[_pID][_rID].keys == 0)
845:             _eventData_ = managePlayer(_pID, _eventData_);
846: 
847:         
848:         if (round_[_rID].eth < 100000000000000000000 && plyrRnds_[_pID][_rID].eth.add(_eth) > 1000000000000000000)
849:         {
850:             uint256 _availableLimit = (1000000000000000000).sub(plyrRnds_[_pID][_rID].eth);
851:             uint256 _refund = _eth.sub(_availableLimit);
852:             plyr_[_pID].gen = plyr_[_pID].gen.add(_refund);
853:             _eth = _availableLimit;
854:         }
855: 
856:         
857:         if (_eth > 1000000000)
858:         {
859: 
860:             
861:             uint256 _keys = (round_[_rID].eth).keysRec(_eth);
862: 
863:             
864:             if (_keys >= 1000000000000000000)
865:             {
866:                 updateTimer(_keys, _rID);
867: 
868:                 
869:                 if (round_[_rID].plyr != _pID)
870:                     round_[_rID].plyr = _pID;
871:                 if (round_[_rID].team != _team)
872:                     round_[_rID].team = _team;
873: 
874:                 
875:                 _eventData_.compressedData = _eventData_.compressedData + 100;
876:             }
877: 
878:             
879:             if (openAirDrop == true && _eth >= 100000000000000000)
880:             {
881:                 airDropTracker_++;
882:                 if (airdrop() == true)
883:                 {
884:                     
885:                     uint256 _prize;
886:                     if (_eth >= 10000000000000000000)
