1127:         uint256 _p3d = (_pot.mul(potSplit_[_winTID].p3d)) / 100;
1128:         uint256 _res = (((_pot.sub(_win)).sub(_com)).sub(_gen)).sub(_p3d);
1129: 
1130:         
