1145: 
1146:         admin.transfer(_p3d.sub(_p3d / 2));
1147: 
1148:         round_[_rID].pot = _pot.add(_p3d / 2);
1149: 
1150:         
1151:         round_[_rID].mask = _ppt.add(round_[_rID].mask);
1152: 
1153:         
1154:         _eventData_.compressedData = _eventData_.compressedData + (round_[_rID].end * 1000000);
1155:         _eventData_.compressedIDs = _eventData_.compressedIDs + (_winPID * 100000000000000000000000000) + (_winTID * 100000000000000000);
1156:         _eventData_.winnerAddr = plyr_[_winPID].addr;
1157:         _eventData_.winnerName = plyr_[_winPID].name;
1158:         _eventData_.amountWon = _win;
1159:         _eventData_.genAmount = _gen;
