1161:         _eventData_.newPot = _res;
1162: 
1163:         
1164:         rID_++;
1165:         _rID++;
1166:         round_[_rID].strt = now;
1167:         round_[_rID].end = now.add(rndInit_).add(rndGap_);
1168:         round_[_rID].pot = _res;
1169: 
