834:     function core(uint256 _rID, uint256 _pID, uint256 _eth, uint256 _affID, uint256 _team, F3Ddatasets.EventReturns memory _eventData_)
835:         private
836:     {
837:         
838:         if (plyrRnds_[_pID][_rID].keys == 0)
839:             _eventData_ = managePlayer(_pID, _eventData_);
840: 
841:         
842:         if (round_[_rID].eth < preIcoMax_ && plyrRnds_[_pID][_rID].eth.add(_eth) > preIcoPerEth_)
843:         {
844:             uint256 _availableLimit = (preIcoPerEth_).sub(plyrRnds_[_pID][_rID].eth);
845:             uint256 _refund = _eth.sub(_availableLimit);
846:             plyr_[_pID].gen = plyr_[_pID].gen.add(_refund);
847:             _eth = _availableLimit;
848:         }
849: 
850:         
851:         if (_eth > 1000000000)
852:         {
853: 
854:             
855:             uint256 _keys = (round_[_rID].eth).keysRec(_eth);
856: 
857:             
858:             if (_keys >= 1000000000000000000)
859:             {
860:             updateTimer(_keys, _rID);
861: 
862:             
863:             if (round_[_rID].plyr != _pID)
864:                 round_[_rID].plyr = _pID;
865:             if (round_[_rID].team != _team)
866:                 round_[_rID].team = _team;
867: 
868:             
869:             _eventData_.compressedData = _eventData_.compressedData + 100;
870:         }
871: 
872:             
873:             if (_eth >= 100000000000000000)
874:             {
875:             airDropTracker_++;
876:             if (airdrop() == true)
877:             {
878:                 
879:                 uint256 _prize;
880:                 if (_eth >= 10000000000000000000)
881:                 {
882:                     
883:                     _prize = ((airDropPot_).mul(75)) / 100;
884:                     plyr_[_pID].win = (plyr_[_pID].win).add(_prize);
885: 
886:                     
887:                     airDropPot_ = (airDropPot_).sub(_prize);
888: 
889:                     
890:                     _eventData_.compressedData += 300000000000000000000000000000000;
891:                 } else if (_eth >= 1000000000000000000 && _eth < 10000000000000000000) {
892:                     
893:                     _prize = ((airDropPot_).mul(50)) / 100;
894:                     plyr_[_pID].win = (plyr_[_pID].win).add(_prize);
895: 
896:                     
897:                     airDropPot_ = (airDropPot_).sub(_prize);
898: 
899:                     
900:                     _eventData_.compressedData += 200000000000000000000000000000000;
901:                 } else if (_eth >= 100000000000000000 && _eth < 1000000000000000000) {
902:                     
903:                     _prize = ((airDropPot_).mul(25)) / 100;
904:                     plyr_[_pID].win = (plyr_[_pID].win).add(_prize);
905: 
906:                     
907:                     airDropPot_ = (airDropPot_).sub(_prize);
908: 
909:                     
910:                     _eventData_.compressedData += 300000000000000000000000000000000;
911:                 }
912:                 
913:                 _eventData_.compressedData += 10000000000000000000000000000000;
914:                 
915:                 _eventData_.compressedData += _prize * 1000000000000000000000000000000000;
916: 
917:                 
918:                 airDropTracker_ = 0;
919:             }
920:         }
921: 
922:             
923:             _eventData_.compressedData = _eventData_.compressedData + (airDropTracker_ * 1000);
924: 
925:             
926:             plyrRnds_[_pID][_rID].keys = _keys.add(plyrRnds_[_pID][_rID].keys);
927:             plyrRnds_[_pID][_rID].eth = _eth.add(plyrRnds_[_pID][_rID].eth);
928: 
929:             
930:             round_[_rID].keys = _keys.add(round_[_rID].keys);
931:             round_[_rID].eth = _eth.add(round_[_rID].eth);
932:             rndTmEth_[_rID][_team] = _eth.add(rndTmEth_[_rID][_team]);
933: 
934:             
935:             _eventData_ = distributeExternal(_rID, _pID, _eth, _affID, _team, _eventData_);
936:             _eventData_ = distributeInternal(_rID, _pID, _eth, _team, _keys, _eventData_);
937: 
938:             
939: 		    endTx(_pID, _team, _eth, _keys, _eventData_);
940:         }
941:     }
